{% extends &#34;base.html&#34; %}

{% block title %}{{ client_name }} - Home{% endblock %}

{% block content %}
&lt;div class=&#34;jumbotron glass&#34;&gt;
    &lt;h1 class=&#34;display-4&#34;&gt;
        &lt;img src=&#34;https://raw.githubusercontent.com/patx/vegy-static/refs/heads/main/gardenfresh/gardenfresh_logo_clear.png&#34;
             style=&#34;max-width: 90%;&#34; alt=&#34;Garden Fresh Logo&#34;&gt;
    &lt;/h1&gt;
    {% if request.session.logged_in %}
    &lt;p class=&#34;lead&#34;&gt;Welcome {{ request.session.email }}!&lt;/p&gt;
    {% else %}
    &lt;p class=&#34;lead&#34;&gt;Order fresh produce easily!&lt;/p&gt;
    {% endif %}
    {% if message == &#34;registered&#34; %}
    &lt;div class=&#34;alert alert-warning&#34;&gt;
        Your account has been successfully registered! A team member will reach out shortly after we approve your account. 
        You will not be able to log in until we approve your new account. 
        &lt;strong&gt;
            You can install our app now if you are on your mobile device. 
            &lt;a href=&#34;https://github.com/patx/vegy-static/raw/refs/heads/main/gardenfresh/install.MP4?raw=True&#34; target=&#34;_blank&#34;&gt;
                Watch the installation video here
            &lt;/a&gt;!
        &lt;/strong&gt; 
        If your account has already been approved you may login below.
    &lt;/div&gt;
    {% endif %}
    {% if request.session.logged_in and request.session.admin %}
    &lt;a class=&#34;btn btn-primary btn-lg&#34; href=&#34;/admin&#34; role=&#34;button&#34;&gt;Orders&lt;/a&gt;
    &lt;a class=&#34;btn btn-primary btn-lg&#34; href=&#34;/items&#34; role=&#34;button&#34;&gt;Items&lt;/a&gt;
    &lt;a class=&#34;btn btn-primary btn-lg&#34; href=&#34;/invites&#34; role=&#34;button&#34;&gt;Accounts&lt;/a&gt;
    &lt;br&gt;&lt;br&gt;&lt;br&gt;
    &lt;a href=&#34;/logout&#34; class=&#34;btn btn-sm btn-outline-warning&#34;&gt;Logout&lt;/a&gt;
    &lt;button type=&#34;button&#34; class=&#34;btn btn-sm btn-outline-warning&#34; data-bs-toggle=&#34;modal&#34; data-bs-target=&#34;#changePasswordModal&#34;&gt;
        Change Password
    &lt;/button&gt;
    {% elif request.session.logged_in %}
    &lt;a class=&#34;btn btn-primary btn-lg&#34; href=&#34;/new_order&#34; role=&#34;button&#34;&gt;Place a New Order&lt;/a&gt;&lt;br&gt;&lt;br&gt;
    &lt;a class=&#34;btn btn-primary btn-lg&#34; href=&#34;/orders&#34; role=&#34;button&#34;&gt;View Your Orders&lt;/a&gt;
    &lt;br&gt;&lt;br&gt;&lt;br&gt;
    &lt;a href=&#34;/logout&#34; class=&#34;btn btn-sm btn-outline-warning&#34;&gt;Logout&lt;/a&gt;
    &lt;button type=&#34;button&#34; class=&#34;btn btn-sm btn-outline-warning&#34; data-bs-toggle=&#34;modal&#34; data-bs-target=&#34;#changePasswordModal&#34;&gt;
        Change Password
    &lt;/button&gt;
    {% else %}
    &lt;a class=&#34;btn btn-primary btn-lg&#34; href=&#34;/login&#34; role=&#34;button&#34;&gt;Login&lt;/a&gt;
    {% endif %}

    &lt;!-- Change Password Modal --&gt;
    {% if request.session.logged_in %}
    &lt;!-- keep your modal + script code here --&gt;
    {% endif %}
&lt;/div&gt;

&lt;style&gt;
/* === Full-page background === */
html, body {
    height: 100%;
    margin: 0;
    background: url(&#34;https://raw.githubusercontent.com/patx/vegy-static/main/screenshots/bg.jpg&#34;)
                no-repeat center center fixed;
    background-size: cover;
}

/* Buttons &amp; text spacing */
a, button { margin-top: 10px; }

/* Logo heading */
h1.display-4 {
    font-family: &#34;Poppins&#34;, sans-serif;
    font-weight: 800;
}

/* Center hero */
.container {
    max-width: 1200px;
    flex: 1;
    display: flex;
    justify-content: center;
    align-items: center;
}

/* Frosted glass effect for content */
.jumbotron.glass {
    text-align: center;
    padding: 2rem;
    width: 100%;
    max-width: 600px;
    margin: 2rem auto;
    background: rgba(255, 255, 255, 0.5); /* semi-transparent white */
    border-radius: 16px;
    box-shadow: 0 8px 24px rgba(0,0,0,0.2);
    backdrop-filter: blur(12px);           /* frosted blur */
    -webkit-backdrop-filter: blur(12px);   /* Safari support */
    margin-top: 200px;
}
h1 { margin-top: 0; }
&lt;/style&gt;
{% endblock %}
